package loop_statements;

public class while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		while(a<=20)
		{
			System.out.println(a);
			a++;
		}
		
		// do_while loop
		int b=1;
		do 
		{
			System.out.println(5+" * "+b+" ="+5*b);
			b++;
		}while(b<=10);
		
		// for loop
		
	}

}
